'use strict';

var _ = require('../');

describe('replaceWith', function () {
  it('creates payload with pathname, query, state', function () {
    expect(_.replaceWith('/some/path', { foo: 'bar' }, { key: 123 }).payload).to.eql({
      pathname: '/some/path',
      query: {
        foo: 'bar'
      },
      state: {
        key: 123
      }
    });
    expect(_.replaceWith().payload).to.eql({
      pathname: undefined,
      query: undefined,
      state: undefined
    });
    expect(_.replaceWith(null).payload).to.eql({
      pathname: null,
      query: undefined,
      state: undefined
    });
    expect(_.replaceWith(false).payload).to.eql({
      pathname: false,
      query: undefined,
      state: undefined
    });
  });
});